class TrafficSimulation {
    constructor() {
        this.isRunning = false;
        this.simulationInterval = null;
        this.canvas = document.getElementById('trafficCanvas');
        this.ctx = this.canvas.getContext('2d');
        this.intersections = [];
        this.vehicles = [];

        this.initializeEventListeners();
        this.initializeCanvas();
        this.addLog('Traffic simulation system initialized');
    }

    initializeEventListeners() {
        document.getElementById('startBtn').addEventListener('click', () => this.startSimulation());
        document.getElementById('stopBtn').addEventListener('click', () => this.stopSimulation());
        document.getElementById('resetBtn').addEventListener('click', () => this.resetSimulation());
    }

    initializeCanvas() {
        // Set up canvas for high DPI displays
        const dpr = window.devicePixelRatio || 1;
        const rect = this.canvas.getBoundingClientRect();
        this.canvas.width = rect.width * dpr;
        this.canvas.height = rect.height * dpr;
        this.ctx.scale(dpr, dpr);
        this.canvas.style.width = rect.width + 'px';
        this.canvas.style.height = rect.height + 'px';

        this.drawInitialCanvas();
    }

    drawInitialCanvas() {
        this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

        // Draw road grid
        this.ctx.strokeStyle = '#666';
        this.ctx.lineWidth = 8;

        // Horizontal roads
        for (let i = 1; i <= 3; i++) {
            const y = (this.canvas.height / 4) * i;
            this.ctx.beginPath();
            this.ctx.moveTo(0, y);
            this.ctx.lineTo(this.canvas.width, y);
            this.ctx.stroke();
        }

        // Vertical roads
        for (let i = 1; i <= 3; i++) {
            const x = (this.canvas.width / 4) * i;
            this.ctx.beginPath();
            this.ctx.moveTo(x, 0);
            this.ctx.lineTo(x, this.canvas.height);
            this.ctx.stroke();
        }

        // Draw intersections
        this.ctx.fillStyle = '#ff6b6b';
        for (let i = 1; i <= 3; i++) {
            for (let j = 1; j <= 3; j++) {
                const x = (this.canvas.width / 4) * i;
                const y = (this.canvas.height / 4) * j;
                this.ctx.beginPath();
                this.ctx.arc(x, y, 15, 0, Math.PI * 2);
                this.ctx.fill();
            }
        }

        // Add title
        this.ctx.fillStyle = '#333';
        this.ctx.font = '16px Arial';
        this.ctx.textAlign = 'center';
        this.ctx.fillText('Traffic Network Visualization', this.canvas.width / 2, 30);
    }

    async fetchTrafficData() {
        try {
            const response = await fetch('/api/traffic-data');
            const data = await response.json();
            return data;
        } catch (error) {
            this.addLog(`Error fetching traffic data: ${error.message}`, 'error');
            return null;
        }
    }

    updateIntersections(trafficData) {
        if (!trafficData) return;

        this.intersections = trafficData.intersections;
        const container = document.getElementById('intersections');
        container.innerHTML = '';

        trafficData.intersections.forEach(intersection => {
            const card = document.createElement('div');
            card.className = `intersection-card ${intersection.status}`;

            card.innerHTML = `
                <div class="intersection-name">${intersection.name}</div>
                <div class="intersection-stats">
                    <span>üöó Vehicles: ${intersection.vehicles}</span>
                    <span>‚è±Ô∏è Wait: ${intersection.avgWaitTime}s</span>
                </div>
                <div class="status-indicator status-${intersection.status}">
                    ${intersection.status}
                </div>
            `;

            container.appendChild(card);
        });
    }

    updateStats(trafficData) {
        if (!trafficData) return;

        const totalVehicles = trafficData.intersections.reduce((sum, int) => sum + int.vehicles, 0);
        const avgWaitTime = Math.round(
            trafficData.intersections.reduce((sum, int) => sum + int.avgWaitTime, 0) / 
            trafficData.intersections.length
        );
        const congestedAreas = trafficData.intersections.filter(int => int.status === 'congested').length;

        document.getElementById('totalVehicles').textContent = totalVehicles;
        document.getElementById('avgWaitTime').textContent = `${avgWaitTime}s`;
        document.getElementById('congestedAreas').textContent = congestedAreas;
    }

    drawVehicles() {
        // Simulate vehicle movement on canvas
        this.drawInitialCanvas();

        // Draw moving vehicles
        this.ctx.fillStyle = '#4CAF50';
        for (let i = 0; i < this.intersections.length; i++) {
            const intersection = this.intersections[i];
            if (!intersection) continue;

            const baseX = (this.canvas.width / 4) * (i + 1);
            const baseY = (this.canvas.height / 4) * (i + 1);

            // Draw vehicles around intersection
            for (let v = 0; v < Math.min(intersection.vehicles, 8); v++) {
                const angle = (v / 8) * Math.PI * 2;
                const radius = 30 + (v * 5);
                const x = baseX + Math.cos(angle) * radius;
                const y = baseY + Math.sin(angle) * radius;

                this.ctx.fillStyle = intersection.status === 'congested' ? '#ff6b6b' : '#4CAF50';
                this.ctx.beginPath();
                this.ctx.arc(x, y, 4, 0, Math.PI * 2);
                this.ctx.fill();
            }
        }
    }

    addLog(message, type = 'info') {
        const logContainer = document.getElementById('logContainer');
        const timestamp = new Date().toLocaleTimeString();
        const logEntry = document.createElement('p');

        let prefix = '';
        switch (type) {
            case 'error':
                prefix = '‚ùå ERROR';
                break;
            case 'warning':
                prefix = '‚ö†Ô∏è WARN';
                break;
            case 'success':
                prefix = '‚úÖ SUCCESS';
                break;
            default:
                prefix = '‚ÑπÔ∏è INFO';
        }

        logEntry.textContent = `[${timestamp}] ${prefix}: ${message}`;
        logContainer.appendChild(logEntry);
        logContainer.scrollTop = logContainer.scrollHeight;

        // Keep only last 20 log entries
        while (logContainer.children.length > 20) {
            logContainer.removeChild(logContainer.firstChild);
        }
    }

    async startSimulation() {
        if (this.isRunning) return;

        this.isRunning = true;
        document.getElementById('startBtn').disabled = true;
        document.getElementById('stopBtn').disabled = false;

        this.addLog('Starting traffic simulation...', 'success');

        this.simulationInterval = setInterval(async () => {
            const trafficData = await this.fetchTrafficData();
            if (trafficData) {
                this.updateIntersections(trafficData);
                this.updateStats(trafficData);
                this.drawVehicles();

                // Log significant events
                const congestedIntersections = trafficData.intersections.filter(int => int.status === 'congested');
                if (congestedIntersections.length > 0) {
                    this.addLog(`Congestion detected at ${congestedIntersections.length} intersection(s)`, 'warning');
                }
            }
        }, 2000);
    }

    stopSimulation() {
        if (!this.isRunning) return;

        this.isRunning = false;
        document.getElementById('startBtn').disabled = false;
        document.getElementById('stopBtn').disabled = true;

        if (this.simulationInterval) {
            clearInterval(this.simulationInterval);
            this.simulationInterval = null;
        }

        this.addLog('Traffic simulation stopped', 'info');
    }

    resetSimulation() {
        this.stopSimulation();

        // Reset all displays
        document.getElementById('totalVehicles').textContent = '0';
        document.getElementById('avgWaitTime').textContent = '0s';
        document.getElementById('congestedAreas').textContent = '0';

        // Clear intersections
        document.getElementById('intersections').innerHTML = '';

        // Reset canvas
        this.drawInitialCanvas();

        // Clear logs except initialization
        document.getElementById('logContainer').innerHTML = '<p>System reset. Ready to start simulation.</p>';

        this.addLog('System reset completed', 'success');
    }
}

// Initialize the simulation when page loads
document.addEventListener('DOMContentLoaded', () => {
    new TrafficSimulation();
});

frontend/web_sim/styles.css.content:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    color: #333;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

header {
    text-align: center;
    margin-bottom: 30px;
    color: white;
}

header h1 {
    font-size: 2.5rem;
    margin-bottom: 10px;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

header p {
    font-size: 1.1rem;
    opacity: 0.9;
}

.dashboard {
    background: white;
    border-radius: 15px;
    padding: 30px;
    box-shadow: 0 10px 30px rgba(0,0,0,0.2);
}

.controls {
    display: flex;
    gap: 15px;
    margin-bottom: 30px;
    justify-content: center;
}

.btn {
    padding: 12px 24px;
    border: none;
    border-radius: 8px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
}

.btn-primary {
    background: #4CAF50;
    color: white;
}

.btn-primary:hover:not(:disabled) {
    background: #45a049;
    transform: translateY(-2px);
}

.btn-secondary {
    background: #f44336;
    color: white;
}

.btn-secondary:hover:not(:disabled) {
    background: #da190b;
    transform: translateY(-2px);
}

.btn-tertiary {
    background: #2196F3;
    color: white;
}

.btn-tertiary:hover:not(:disabled) {
    background: #0b7dda;
    transform: translateY(-2px);
}

.btn:disabled {
    opacity: 0.6;
    cursor: not-allowed;
}

.stats-overview {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 20px;
    margin-bottom: 30px;
}

.stat-card {
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    padding: 20px;
    border-radius: 10px;
    text-align: center;
    border: 2px solid #e0e0e0;
}

.stat-card h3 {
    font-size: 0.9rem;
    color: #666;
    margin-bottom: 10px;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.stat-card span {
    font-size: 2rem;
    font-weight: bold;
    color: #333;
}

.traffic-grid {
    margin-bottom: 30px;
}

.traffic-grid h2 {
    margin-bottom: 20px;
    color: #333;
}

.intersections-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 20px;
}

.intersection-card {
    background: #f8f9fa;
    border: 2px solid #e9ecef;
    border-radius: 10px;
    padding: 20px;
    transition: all 0.3s ease;
}

.intersection-card:hover {
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
}

.intersection-card.congested {
    border-color: #dc3545;
    background: #fff5f5;
}

.intersection-card.normal {
    border-color: #28a745;
    background: #f8fff9;
}

.intersection-name {
    font-size: 1.2rem;
    font-weight: bold;
    margin-bottom: 15px;
    color: #333;
}

.intersection-stats {
    display: flex;
    justify-content: space-between;
    margin-bottom: 10px;
}

.intersection-stats span {
    font-size: 0.9rem;
    color: #666;
}

.status-indicator {
    display: inline-block;
    padding: 4px 12px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: bold;
    text-transform: uppercase;
}

.status-normal {
    background: #d4edda;
    color: #155724;
}

.status-congested {
    background: #f8d7da;
    color: #721c24;
}

.simulation-area {
    margin-bottom: 30px;
}

.simulation-area h2 {
    margin-bottom: 20px;
    color: #333;
}

#trafficCanvas {
    border: 2px solid #ddd;
    border-radius: 10px;
    background: #f8f9fa;
    display: block;
    margin: 0 auto;
    max-width: 100%;
}

.logs {
    background: #f8f9fa;
    border-radius: 10px;
    padding: 20px;
}

.logs h3 {
    margin-bottom: 15px;
    color: #333;
}

.log-container {
    background: #2c3e50;
    color: #ecf0f1;
    padding: 15px;
    border-radius: 5px;
    font-family: 'Courier New', monospace;
    font-size: 0.9rem;
    max-height: 200px;
    overflow-y: auto;
}

.log-container p {
    margin-bottom: 5px;
    padding: 2px 0;
}

.log-container p:last-child {
    margin-bottom: 0;
}

@media (max-width: 768px) {
    .container {
        padding: 10px;
    }

    header h1 {
        font-size: 2rem;
    }

    .controls {
        flex-direction: column;
        align-items: center;
    }

    .btn {
        width: 200px;
    }

    #trafficCanvas {
        width: 100%;
        height: auto;
    }
}

server.js.content:
const express = require('express');
const path = require('path');

const app = express();
const PORT = 5000;

// Middleware to disable caching for development
app.use((req, res, next) => {
  res.setHeader('Cache-Control', 'no-cache, no-store, must-revalidate');
  res.setHeader('Pragma', 'no-cache');
  res.setHeader('Expires', '0');
  next();
});

// Serve static files from frontend/web_sim
app.use(express.static(path.join(__dirname, 'frontend', 'web_sim')));

// API endpoint for traffic data simulation
app.get('/api/traffic-data', (req, res) => {
  // Simulate traffic data
  const trafficData = {
    intersections: [
      {
        id: 1,
        name: 'Main St & 1st Ave',
        vehicles: Math.floor(Math.random() * 20) + 5,
        avgWaitTime: Math.floor(Math.random() * 30) + 10,
        status: Math.random() > 0.7 ? 'congested' : 'normal'
      },
      {
        id: 2,
        name: 'Oak Rd & 2nd Ave',
        vehicles: Math.floor(Math.random() * 15) + 3,
        avgWaitTime: Math.floor(Math.random() * 25) + 8,
        status: Math.random() > 0.8 ? 'congested' : 'normal'
      },
      {
        id: 3,
        name: 'Pine St & 3rd Ave',
        vehicles: Math.floor(Math.random() * 18) + 7,
        avgWaitTime: Math.floor(Math.random() * 35) + 12,
        status: Math.random() > 0.6 ? 'congested' : 'normal'
      }
    ],
    timestamp: new Date().toISOString()
  };

  res.json(trafficData);
});

// Catch all handler for SPA
app.get('*', (req, res) => {
  res.sendFile(path.join(__dirname, 'frontend', 'web_sim', 'index.html'));
});

app.listen(PORT, '0.0.0.0', () => {
  console.log(`Smart Traffic Management Simulation server running on http://0.0.0.0:${PORT}`);
});
